/* eslint-disable react/prop-types */
import React, { useState } from 'react'
import { connect } from 'react-redux'
import Select from 'react-select'
import { makeStyles } from '@material-ui/core/styles'
import {
  Grid,
  Collapse,
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Dialog,
  DialogActions,
  DialogTitle,
  Button,
  IconButton,
  Typography,
  Modal,
  Backdrop,
  Fade,
  FormControl,
  FormControlLabel,
  TextField,
  Switch,
  Tooltip,
  Box,
  Checkbox
} from '@material-ui/core'
import AddCircleIcon from '@material-ui/icons/AddCircle'
import SaveIcon from '@material-ui/icons/Save'
import CancelIcon from '@material-ui/icons/Cancel'
import DeleteIcon from '@material-ui/icons/Delete'
import EditIcon from '@material-ui/icons/Edit'
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward'
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward'

const useStyles = makeStyles(theme => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    margin: theme.spacing(2),
    padding: theme.spacing(2)
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3)
  },
  field: {
    margin: 0,
    padding: 0
  },
  iconButton: {
    padding: 0,
    margin: 0
  }
}))

const ReportItems = ({
  handleSaveItems,
  handleCloseItems,
  openItems,
  itemsAvailable
}) => {
  const classes = useStyles()

  const options = itemsAvailable.map(item => {
    return { ...item, value: item.itemNo, label: item.itemName }
  })

  return (
    <Modal
      id='items'
      className={classes.modal}
      open={openItems}
      onClose={handleCloseItems}
      closeAfterTransition
      BackdropComponent={Backdrop}
      BackdropProps={{
        timeout: 500
      }}
    >
      <Fade in={openItems}>
        <div className={classes.paper}>
          <Grid container spacing={1} justify='space-between'>
            <Grid item xs={12}>
              <Select
                className='itemsSelect'
                classNamePrefix='select'
                isClearable={true}
                isSearchable={true}
                name='items'
                options={options}
              />
            </Grid>

            <Grid item xs={12}>
                        <TextField
              className={classes.textField}
              variant='outlined'
              name='quantity'
              label='Quantity'
              type='number'
              defaultValue={1}
              onChange={changeField}
            />
              <Tooltip title='Add New'>
                <IconButton onClick={() => addNewItems()}>
                  <AddCircleIcon color='secondary' />
                </IconButton>
              </Tooltip>
            </Grid>

            <Grid
              container
              direction='row'
              justify='flex-end'
              alignItems='flex-end'
            >
              <Button
                variant='contained'
                color='secondary'
                style={{ margin: 10 }}
                onClick={handleCloseItems}
                startIcon={<CancelIcon />}
              >
                Cancel
              </Button>
              <Button
                variant='contained'
                color='primary'
                style={{ margin: 10 }}
                onClick={() => handleSaveItems()}
                startIcon={<SaveIcon />}
              >
                Save
              </Button>
            </Grid>
          </Grid>
        </div>
      </Fade>
    </Modal>
  )
}

export default connect(state => state)(ReportItems)
