import React, { useState, useEffect } from 'react'
import axiosClient from '../../src/axiosClient'
import { useSnackbar } from 'notistack'
import { connect } from 'react-redux'
import { flatten } from 'lodash'
import { fade, makeStyles, useTheme } from '@material-ui/core/styles'
import Link from '../../src/Link'
import TopBar from '../../components/TopBar'
import numeral from 'numeral'
const priceFormat = '$0.00'
import moment from 'moment-timezone'
const dateFormat = 'YYYY-MM-DDTHH:mm:SS'
const dateDisplay = 'dddd MMM DD hh:mm a'
import {
  Container,
  Grid,
  Checkbox,
  Typography,
  TextField,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Table,
  TableBody,
  TableCell,
  TableRow,
  Button
} from '@material-ui/core'
/*
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
  TimelineOppositeContent
} from '@material-ui/lab'
*/
import { getLangString } from '../../components/Lang'
import CancelIcon from '@material-ui/icons/Cancel'
import CheckCircleIcon from '@material-ui/icons/CheckCircle'

const useStyles = makeStyles(theme => ({
  toolbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar
  },
  formGroup: {padding: 20,
    backgroundColor: '#eee',
    border: '1px solid #ccc'
  },
  center: {
    textAlign: 'center'
  },
  content: {
    textAlign: 'center',
    flexGrow: 1,
    padding: theme.spacing(7)
  },
  root: {
    flexGrow: 1
  },
  center: {
    textAlign: 'center',
    flexGrow: 1
  },
  lines: {
    display: 'flex',
    justifyContent: 'flex-end',
    flexGrow: 1,
    borderTop: '1px solid #ccc',
    borderLeft: '1px solid #ccc',
    borderRight: '1px solid #ccc',
    '&:last-child': {
      reportBottom: '1px solid #ccc'
    }
  },
  nolines: {
    reportBottom: '2px solid white'
  },
  flexGrid: {
    display: 'flex',
    justifyContent: 'flex-end',
    flexGrow: 1
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  button: {
    margin: theme.spacing(1)
  }
}))

const Report = ({ propsReport, dispatch, lang }) => {
  const classes = useStyles()
  const theme = useTheme()
  const [report, setReport] = React.useState(propsReport)
  const langSuffix = lang ? lang.substring(0, 2) : 'en'
  const { enqueueSnackbar } = useSnackbar()

  useEffect(() => {
    document.title = report.customerName
  })

  const updateReport = async updateReport => {
    await axiosClient
      .patch('/reports', updateReport)
      .then(res => {
        enqueueSnackbar(getLangString('report.updated', lang), {
          variant: 'success'
        })
        setReport(updateReport)
      })
      .catch(err => {
        enqueueSnackbar(getLangString('report.updateError', lang), {
          variant: 'error'
        })
      })
  }

  return (
    <Container>
      <TopBar />
      <div className={classes.content}>
        <div className={classes.toolbar} />
        <Grid item container xs={12} spacing={2} justify='space-between' className={classes.formGroup}>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              id='customerName'
              label='Customer Name'
              value={report && report.customerName ? report.customerName : ''}
              readOnly
            />
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              id='customerStreet'
              label='Street Address'
              value={
                report && report.customerStreet ? report.customerStreet : ''
              }
              readOnly
            />
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              id='customerCity'
              label='City'
              value={report && report.customerCity ? report.customerCity : ''}
              readOnly
            />
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              id='customerState'
              label='State'
              value={report && report.customerState ? report.customerState : ''}
              readOnly
            />
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              id='customerZip'
              label='Zip'
              value={report && report.customerZip ? report.customerZip : ''}
              readOnly
            />
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              id='customerPhone'
              label='Phone'
              value={report && report.customerPhone ? report.customerPhone : ''}
              readOnly
            />
          </Grid>
        </Grid>

        {/*
        <Grid item container xs={12} spacing={2} justify='flex-end'>
          <Grid item>
            <Timeline>
              {report.timeline.map((timestamp, index) => (
                <TimelineItem key={index}>
                  <TimelineSeparator>
                    <TimelineDot />
                    {index < report.timeline.length - 1 ? (
                      <TimelineConnector />
                    ) : (
                      ''
                    )}
                  </TimelineSeparator>
                  <TimelineContent>
                    <Typography>
                      {timestamp[`description_${langSuffix}`]}<p/>
                      {moment(timestamp.timestamp, dateFormat)
                        .locale(lang.substring(0, 2))
                        .format(dateDisplay)}
                    </Typography>
                  </TimelineContent>
                </TimelineItem>
              ))}
            </Timeline>
          </Grid>
          {report.timeline.length <= 1 ? (
            <Grid item>
              <Button
                onClick={reportDelivered}
                variant='contained'
                color='primary'
                className={classes.button}
                startIcon={<CheckCircleIcon />}
              >
                {getLangString('report.delivered', lang)}
              </Button>
              <Button
                onClick={reportCancelled}
                variant='contained'
                color='secondary'
                className={classes.button}
                startIcon={<CancelIcon />}
              >
                {getLangString('report.cancelled', lang)}
              </Button>
            </Grid>
          ) : (
            ''
          )}
        </Grid>
        */}
      </div>
    </Container>
  )
}

export async function getServerSideProps (context) {
  const { id } = context.params
  const propsReport = await axiosClient
    .get('/reports/' + id)
    .then(response => response.data)

  return {
    props: {
      propsReport
    }
  }
}

export default connect(state => state)(Report)
