import React, { useState, useEffect } from 'react'
import axiosClient from '../../src/axiosClient'
import { useSnackbar } from 'notistack'
import { connect } from 'react-redux'
import { flatten } from 'lodash'
import { fade, makeStyles, useTheme } from '@material-ui/core/styles'
import Link from '../../src/Link'
import TopBar from '../../components/TopBar'
import numeral from 'numeral'
const priceFormat = '$0.00'
import moment from 'moment-timezone'
const dateFormat = 'YYYY-MM-DDTHH:mm:SS'
const dateDisplay = 'dddd MMM DD hh:mm a'
import {
  Box,
  Container,
  Grid,
  Checkbox,
  Typography,
  TextField,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Table,
  TableBody,
  TableCell,
  TableRow,
  Button,
  FormControlLabel
} from '@material-ui/core'
/*
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
  TimelineOppositeContent
} from '@material-ui/lab'
*/
import { getLangString } from '../../components/Lang'
import CancelIcon from '@material-ui/icons/Cancel'
import CheckCircleIcon from '@material-ui/icons/CheckCircle'
import DateFnsUtils from '@date-io/date-fns'
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker
} from '@material-ui/pickers'

const useStyles = makeStyles(theme => ({
  toolbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar
  },
  formGroup: {
    margin: 20,
    padding: 20,
    backgroundColor: '#fafafa',
    border: '1px solid #ccc'
  },
  formGroupTop: {
    margin: 20,
    padding: 20
  },
  center: {
    textAlign: 'center'
  },
  content: {
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexGrow: 1,
    paddingRight: theme.spacing(5)
  },
  root: {
    padding: 0,
    flexGrow: 1
  },
  center: {
    textAlign: 'center',
    flexGrow: 1
  },
  lines: {
    display: 'flex',
    justifyContent: 'flex-end',
    flexGrow: 1,
    borderTop: '1px solid #ccc',
    borderLeft: '1px solid #ccc',
    borderRight: '1px solid #ccc',
    '&:last-child': {
      reportBottom: '1px solid #ccc'
    }
  },
  nolines: {
    reportBottom: '2px solid white'
  },
  flexGrid: {
    display: 'flex',
    justifyContent: 'flex-end',
    flexGrow: 1
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  button: {
    margin: theme.spacing(1)
  },
  textField: {
    margin: 2,
    padding: 2
  },
  checkbox: {
    marginTop: 6,
    marginBottom: 6,
    marginLeft: 16,
    padding: 6
  }
}))

const Report = ({ propsReport, dispatch, lang }) => {
  const classes = useStyles()
  const theme = useTheme()
  const [report, setReport] = React.useState(propsReport)
  const langSuffix = lang ? lang.substring(0, 2) : 'en'
  const { enqueueSnackbar } = useSnackbar()

  const [selectedDate, setSelectedDate] = React.useState(
    new Date('2014-08-18T21:11:54')
  )

  const handleDateChange = date => {
    setSelectedDate(date)
  }

  useEffect(() => {
    document.title = report.customerName
  })

  const changeValue = (name, value) => {
    const updated = {
      ...report,
      [name]: value
    }
    setReport(updated)
    //updateReport(updated)
  }

  const changeField = event => {
    changeValue(event.target.name, event.target.value)
  }

  const changeCheckbox = event => {
    changeValue(event.target.name, event.target.checked)
  }

  const updateReport = async updateReport => {
    await axiosClient
      .patch('/reports', updateReport)
      .then(res => {
        enqueueSnackbar('Report Updated', {
          variant: 'success'
        })
        setReport(updateReport)
      })
      .catch(err => {
        enqueueSnackbar('Update Error', {
          variant: 'error'
        })
      })
  }

  return (
    <Container className={classes.root}>
      <TopBar />
      <div className={classes.content}>
        <Grid
          container
          direction='row'
          xs={12}
          spacing={2}
          alignItems='flex-end'
          justify='space-between'
          className={classes.formGroupTop}
        >
          <Grid item>
            <TextField
              className={classes.textField}
              name='job'
              label='Job#'
              defaultValue={report.job ? report.job : ''}
              onChange={changeField}
            />
          </Grid>
          <Grid item>
            <MuiPickersUtilsProvider utils={DateFnsUtils}>
              <KeyboardDatePicker
                disableToolbar
                variant='inline'
                format='MM/dd/yyyy'
                id='date'
                label='Date'
                value={selectedDate}
                onChange={handleDateChange}
              />
            </MuiPickersUtilsProvider>
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              name='po'
              label='PO#'
              defaultValue={report.po ? report.po : ''}
              onChange={changeField}
            />
          </Grid>
        </Grid>

        <Grid
          container
          direction='row'
          xs={12}
          spacing={2}
          justify='space-between'
          className={classes.formGroup}
        >
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              name='customerName'
              label='Customer Name'
              defaultValue={report.customerName ? report.customerName : ''}
              onChange={changeField}
            />
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              name='customerStreet'
              label='Street Address'
              defaultValue={report.customerStreet ? report.customerStreet : ''}
              onChange={changeField}
            />
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              name='customerCity'
              label='City'
              defaultValue={report.customerCity ? report.customerCity : ''}
              onChange={changeField}
            />
          </Grid>
          <Grid item>
            <TextField
              className={classes.textField}
              variant='outlined'
              name='customerState'
              label='State'
